mapper.select:
select count(*) as totCnt
from statistic.requestinfo info
where left(info.createDate, 2) = #{year};

    	SELECT count(*) AS totCnt
		FROM statistic.requestinfo AS info
		WHERE left(info.createDate, 4) = #{month};

SELECT count(*) AS totCnt
FROM statistic.requestinfo AS info
WHERE left(info.createDate, 6) = #{date};
